import { React, useState } from "react";

import {
  FormControl,
  FormLabel,
  FormErrorMessage,
  Input,
  Center,
  Button,
  TableContainer,
  Table,
  TableCaption,
  Thead,
  Tr,
  Th,
  Tbody,
  Td,
  Flex,
  Stack,
  InputGroup,
  InputLeftAddon,
} from "@chakra-ui/react";

const AddRecipe = () => {
  const [recipeName, setRecipeName] = useState("");
  const [ingredients, setIngredients] = useState([]);

  const handleRecipeInputChange = (e) => {
    setRecipeName(e.target.value);
  };

  const handleIngredientsInputChange = (e) => {
    console.log(ingredients, ingredientsError);
    setIngredients(e.target.value);
  };

  // const handleInputChange = (e) => setRecipeName(e.target.value);

  const recipeNameError = recipeName === "";
  const ingredientsError = ingredients === [];

  return (
    <>
      <FormControl isInvalid={recipeNameError || ingredientsError}>
        <FormLabel
          style={{
            textAlign: "center",
            fontSize: "max(1.5vw, 15px)",
            marginTop: "25px",
          }}
        >
          Name
        </FormLabel>
        <Center>
          <Input
            errorBorderColor="red.300"
            width="40%"
            borderColor="green.400"
            onChange={handleRecipeInputChange}
          />
        </Center>
        {recipeNameError ? (
          <Center>
            <FormErrorMessage>Name is required.</FormErrorMessage>
          </Center>
        ) : (
          <></>
        )}

        <FormLabel
          style={{
            textAlign: "center",
            fontSize: "max(1.5vw, 15px)",
            marginTop: "25px",
          }}
        >
          Ingredients:
        </FormLabel>

        <Center>
          <Stack spacing={4}>
            <InputGroup>
              <InputLeftAddon children="Name" w="25%" />
              <Input type="text" />
            </InputGroup>

            <InputGroup size="sm">
              <InputLeftAddon children="Amount (g)" w="25%" />
              <Input />
            </InputGroup>
          </Stack>

          {/* <TableContainer>
            <Table>
              <Thead>
                <Tr>
                  <Th>Ingredient Name</Th>
                  <Th isNumeric>Amount (grams)</Th>
                </Tr>
              </Thead>

              <Tbody>
                <Tr>
                  <Td>
                    <Input borderColor="green.400" />
                  </Td>
                  <Td>
                    <Input borderColor="green.400" />
                  </Td>
                </Tr>
              </Tbody>
            </Table>
          </TableContainer> */}
        </Center>
        <Center></Center>

        <Center>
          <Button mt={4} colorScheme="teal" type="submit">
            Submit
          </Button>
        </Center>
      </FormControl>
    </>
  );
};

export default AddRecipe;
