import { React, useState } from "react";

import {
  FormControl,
  FormLabel,
  FormErrorMessage,
  Input,
  Center,
  Button,
} from "@chakra-ui/react";

const AddRecipe = () => {
  const [recipeName, setRecipeName] = useState("");
  const [ingredients, setIngredients] = useState([
    {
      ingredientName: "",
      amount: 0,
    },
  ]);

  const handleRecipeInputChange = (e) => {
    setRecipeName(e.target.value);
  };

  const handleIngredientsInputChange = (e) => {
    setIngredients(e.target.value);
  };

  const handleInputChange = (e, form) => {
    if (form === "ingredients") {
      setIngredients(e.target.value);
    } else if (form === "recipeName") {
      setRecipeName(e.target.value);
    }
  };

  // const handleInputChange = (e) => setRecipeName(e.target.value);

  const recipeNameError = recipeName === "";
  const ingredientsError = ingredients === {};

  return (
    <>
      <FormControl isInvalid={recipeNameError}>
        <FormLabel
          style={{
            textAlign: "center",
            fontSize: "max(1.5vw, 15px)",
            marginTop: "25px",
          }}
        >
          Name
        </FormLabel>
        <Center>
          <Input
            errorBorderColor="red.300"
            width="40%"
            borderColor="green.400"
            onChange={handleInputChange}
          />
        </Center>
        <Center>
          <FormErrorMessage>Name is required.</FormErrorMessage>
        </Center>

        <FormControl isInvalid={ingredientsError}>
          <FormLabel
            style={{
              textAlign: "center",
              fontSize: "max(1.5vw, 15px)",
              marginTop: "25px",
            }}
          >
            Ingredients:
          </FormLabel>
          <Center>
            <Input
              errorBorderColor="red.300"
              width="40%"
              borderColor="green.400"
              onChange={handleInputChange}
            />
          </Center>
        </FormControl>
        <Center>
          <Button mt={4} colorScheme="teal" type="submit">
            Submit
          </Button>
        </Center>
      </FormControl>
    </>
  );
};

export default AddRecipe;
